"use strict";chrome.contextMenus.create({title:chrome.i18n.getMessage("create_qr_context"),type:"normal",contexts:["all"],onclick:function(e){var o="popup.html";if(e.linkUrl){var t=encodeURIComponent(e.linkUrl),n=encodeURIComponent(e.selectionText);chrome.tabs.create({url:o+"?title="+n+"&url="+t})}else chrome.tabs.getSelected(null,function(e){var t=encodeURIComponent(e.url),n=encodeURIComponent(e.title);chrome.tabs.create({url:o+"?title="+n+"&url="+t})})}});